    public int longestValidParenthesesOld(String s) {
        int longest = 0;
        int firstIndex = s.indexOf("(");
        if (firstIndex == -1) {
            return 0;
        }
        do {
            String str = s.substring(firstIndex);
            Stack<Integer> stack = new Stack<Integer>();
            int foundLength = -1;
            for (int i = 0; i < str.length(); i++) {
                if (str.charAt(i) == '(') {
                    stack.push(1);
                } else if (str.charAt(i) == ')') {
                    if (stack.isEmpty()) {
                        longest = i<longest?longest: i;
                        break;
                    }
                    stack.pop();
                }
                if(stack.isEmpty()){
                    longest = (i+1)<longest?longest: i+1;
                }
            }

            if(s.length()>1) {
                s = str.substring(1);
            }else{
                break;
            }
            firstIndex = s.indexOf("(");
        }while(firstIndex != -1);

        return longest;
    }





    public int longestValidParenthesesUsingStack(String s){
        if(s.length() == 0) return 0;
        int start = s.indexOf("(");
        if(start == -1){
            return 0;
        }
        int longest = 0;
        while(start < s.length()){
            String str = s.substring(start);
            int lon = findLongest(str);
            if(lon != 0){
                longest = lon>longest?lon:longest;
            }
            start++;
            str = s.substring(start);
            if(str.indexOf("(") == -1){
                break;
            }
            start += str.indexOf("(");
        }
        return longest;
    }

    public int findLongest(String s){
        int max = 0;
        int count = 0;
        for(int i=0; i<s.length(); i++){
            if(s.charAt(i) == '('){
                count++;
            } else if(s.charAt(i) == ')'){
                count--;
            }
            if(count<0){
                return max;
            }
            if(count == 0){
                max = i+1;
            }
        }
        return max;
    }
